mapper.select:
select gt.gym_id, gt.gym_name, gt.gym_address, gt.gym_tel, gt.businessnumber, gt.gym_price,
got.gym_owner_id
from gym_tb gt left outer join gym_owner_tb got on(got.gym_id = gt.gym_id) where gt.gym_id = #{gymId}

mapper.delete:
delete
from
	gym_like_tb
where
	gym_id = #{gymId}
and user_id = #{userId}
